package Variables;

public class OperadoresAritmeticos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Resto %
		
		int n1=100;
		System.out.println(n1%2);

		//++ --  n=n+1
		int n2=10;
		System.out.println(n2++); //Primero hace println pero no hace la suma
		System.out.println(+n2); //Hace la opraci√≥n de suma y luego println		
		System.out.println(n2);
	
		//  += *=
		int v2=5;
		System.out.println(v2*=4);  //v2=v2*4
		
		
	}
	
	
	

}
